import { useActionState } from "react";

async function increment(previousState: number): Promise<number> {
  return previousState + 1;
}

async function decrement(previousState: number): Promise<number> {
  return previousState - 1;
}

function countState(actionName: string) {
  if (actionName === "increment") {
    return increment;
  } else {
    return decrement;
  }
}

export function CounterApp() {
  const [state, formAction] = useActionState(
    (previousState:number, formData: FormData) => {
      const actionName = formData.get("action");
      const selectedAction = countState(actionName);
      return selectedAction(previousState);
    },
    0 // initial state
  );

  return (
    <form>
      <br />
      <p>State: {state}</p>
      <button formAction={formAction} name="action" value="increment">
        Increment
      </button>
      <button formAction={formAction} name="action" value="decrement">
        Decrement
      </button>
      <br />
    </form>
  );
}
