import {use, useActionState } from "react";


async function increment(previousState: number ): Promise<number> {
  return previousState + 1;
}

async function decrement(previousState: number): Promise<number> {
  return previousState - 1;
}

//incrementとdecrementを合わせたやつ
async function countState(previousState: number): Promise<number> {
const actionName =use(formAction);
  if (actionName === "increment") {
    return increment(previousState);
  } else  {
    return decrement(previousState);
  }
}

export function CounterApp() {

  const [state, formAction] = useActionState(countState,0);

  return (
    <form>
      <br />
      {state}
      <button formAction={formAction} name="action" value="increment">stateIncrement</button>
      <button formAction={formAction} name="action" value="decrement">stateDecrement</button>
      <br />
    </form>
  );
}
