import { use, useActionState } from "react";

async function increment(previousState: number): Promise<number> {
  return previousState + 1;
}

async function decrement(previousState: number): Promise<number> {
  return previousState - 1;
}

// incrementとdecrementを合わせたやつ
async function countState(previousState: number, actionName: string): Promise<number> {
  if (actionName === "increment") {
    return increment(previousState);
  } else if (actionName === "decrement") {
    return decrement(previousState);
  }
  return previousState; // デフォルトで現在の状態を返す
}

export function CounterApp() {
  const [state, formAction] = useActionState<number>(async (previousState, formData) => {
    // use APIを使ってボタンのaction値を取得
    const actionName = formData.get("action") as string;
    return use(countState(previousState, actionName));
  }, 0);

  return (
    <form>
      <br />
      {state}
      <button formAction={formAction} name="action" value="increment">
        stateIncrement
      </button>
      <button formAction={formAction} name="action" value="decrement">
        stateDecrement
      </button>
      <br />
    </form>
  );
}
