import { useActionState } from 'react';

// Increment
async function increment(previousState: number) {
  return previousState + 1;
}

// Decrement
async function decrement(previousState: number) {
  return previousState - 1;
}

// Reset
async function reset(previousState: number) {
  return 0;
}

export function CounterApp() {
  // useActionState を使用して、状態管理とアクションを定義
  const [state, incrementAction] = useActionState(increment, 0);
  const [_, decrementAction] = useActionState(decrement, state);
  const [__, resetAction] = useActionState(reset, 0);

  return (
    <form>
      <div>
        <p>{state}</p>
        <button formAction={incrementAction}>
          Increment
        </button>
        <button  formAction={decrementAction}>
          Decrement
        </button>
        <button  formAction={resetAction}>
          Reset
        </button>
      </div>
    </form>
  );
}
