import { useActionState } from "react";

// インクリメント用のアクション
async function increment(previousState: number, formData: FormData) {
  return previousState + 1;
}

// デクリメント用のアクション
async function decrement(previousState: number, formData: FormData) {
  return previousState - 1;
}

// リセット用のアクション
async function reset(previousState: number, formData: FormData) {
  return 0;
}

export const CounterApp: React.FC = () => {
  // 初期状態を0とする
  const [count, incrementAction] = useActionState(increment, 0);
  const [_, decrementAction] = useActionState(decrement, 0);
  const [__, resetAction] = useActionState(reset, 0);

  return (
    <div>
      <h1>Counter: {count}</h1>
      <form method="post">
        {/* インクリメントボタン */}
        <button type="submit" formAction={incrementAction}>
          Increment
        </button>
        {/* デクリメントボタン */}
        <button type="submit" formAction={decrementAction}>
          Decrement
        </button>
        {/* リセットボタン */}
        <button type="submit" formAction={resetAction}>
          Reset
        </button>
      </form>
    </div>
  );
}
