import React, { useState } from "react";
import { useActionState } from "react";

// 非同期関数：increment と decrement
async function increment(previousState: number): Promise<number> {
  return previousState + 1;
}

async function decrement(previousState: number): Promise<number> {
  return previousState - 1;
}

function IncrementButton({ formAction }: { formAction: () => void }) {
  return (
    <form>
      <p>Incrementボタンで合計値を増加させます</p>
      <button type="submit" formAction={formAction}>Increment</button>
    </form>
  );
}

function DecrementButton({ formAction }: { formAction: () => void }) {
  return (
    <form>
      <p>Decrementボタンで合計値を減少させます</p>
      <button type="submit" formAction={formAction}>Decrement</button>
    </form>
  );
}

export function CounterApp() {
  // 合計値をstateで管理
  const [total, setTotal] = useState(0);

  // useActionStateフックを使ってインクリメントとデクリメント
  const [incrementState, incrementAction] = useActionState(increment, total);
  const [decrementState, decrementAction] = useActionState(decrement, total);

  // 合計値が更新される度に、totalを更新
  React.useEffect(() => {
    setTotal(incrementState);  // incrementStateが更新された時にtotalを更新
  }, [incrementState]);

  React.useEffect(() => {
    setTotal(decrementState);  // decrementStateが更新された時にtotalを更新
  }, [decrementState]);

  return (
    <div>
      <h1>合計値: {total}</h1>
      <IncrementButton formAction={incrementAction} />
      <DecrementButton formAction={decrementAction} />
    </div>
  );
}
