import { useActionState } from "react";

// increment 関数
async function increment(previousState: number): Promise<number> {
  return previousState + 1;
}

// decrement 関数
async function decrement(previousState: number): Promise<number> {
  return previousState - 1;
}

// countState 関数
async function countState(previousState: number, formData: FormData): Promise<number> {
     formData.forEach((value, key) => {
        console.log(`Key: ${key}, Value: ${value}`);
      });
  // formDataから'action'の値を取得
  const action = formData.get("action") as string; // 'action' の値は文字列として取得

  console.log("Received action:", action);

  if (action === "increment") {
    return increment(previousState);
  } else if (action === "decrement") {
    return decrement(previousState);
  }

  // 'action'がない場合、そのままの状態を返す
  return previousState;
}

export function CounterApp() {
  // useActionState を使ってカウントの状態とアクションを管理
  const [state, formAction] = useActionState(countState, 0);

  return (
    <form>
      <br />
      {state}
      <button type="submit" name="action" value="increment" formAction={formAction}>
        Increment
      </button>
      <button type="submit" name="action" value="decrement" formAction={formAction}>
        Decrement
      </button>
      <br />
    </form>
  );
}
