//React19
import { useActionState,useState } from "react";

async function increment(previousState:number): Promise<number> {
  return previousState + 1;
}

async function decrement(previousState:number): Promise<number> {
  return previousState - 1;
}

async function reset(previousState:number): Promise<number> {
    return previousState =0;
}

function CountRate({ rate }: { rate: number }) {
    return <div>WinRate: {rate.toFixed(2)}</div>;
}

type RatingType = {
    All:number,
    Win:number,
    Lose:number
}
export function Counter() {
    const [rating, setRating] = useState<RatingType>({All:0,Win:0,Lose:0});
    const winGame=rating.Win;
    const loseGame=rating.Lose;
    const allGame=rating.All;

    const [stateWin, stateWinIncrement] = useActionState( increment,rating.Win);
    const [stateLose, stateLoseIncrement] = useActionState( increment,rating.Lose);
    const [stateAll, stateAllIncrement] = useActionState( increment,rating.All);

    const [stateDec, stateWinDecrement] = useActionState( decrement,rating.Win);
    const [stateLoseDecrement] = useActionState( decrement,rating.Lose);

    const rate = winGame + loseGame > 0 ? winGame / (allGame) : 0;

    return (

    <form>

    Win:{rating.Win}
    <button formAction={stateIncrement}>stateIncrement</button>
    <button formAction={stateDecrement}>stateDecrement</button>

    Lose:{rating.Lose}
    <button formAction={stateIncrement}>stateLoseIncrement</button>
    <button formAction={stateDecrement}>stateLoseDecrement</button>

    All Game:{rating.All}
    <CountRate rate={rate}/>
    </form>
    )
}
