import { useActionState } from "react";


async function increment(previousState: number, formData: FormData): Promise<number> {
  return previousState + 1;
}

async function decrement(previousState: number, formData: FormData): Promise<number> {
  return previousState - 1;
}

//incrementとdecrementを合わせたやつ
async function countState(previousState: number, formData: FormData): Promise<number> {
  const actionName = formData.get("action") as string; // Get the action name from the form data

  if (actionName === "increment") {
    return increment(previousState, formData);
  } else if (actionName === "decrement") {
    return decrement(previousState, formData);
  } else {
    throw new Error(`Unsupported action: ${actionName}`);
  }
}


export function CounterApp() {
  const [state, formAction] = useActionState(countState,0);

  return (
    <form>
      <br />
      {state}
      <button formAction={formAction} name="action" value="increment">stateIncrement</button>
      <button formAction={formAction} name="action" value="decrement">stateDecrement</button>
      <br />
    </form>
  );
}
