import { useOptimistic, useState, useRef } from "react";
import { useFormStatus } from "react-dom";

// UserNameSubmitFormコンポーネントで送信中メッセージを表示
function UserNameSubmitForm({ name }: { name: string }) {
  const { pending } = useFormStatus();  // pendingの状態を取得
  return (
    <div>
      <p>{pending ? `Submitting ${name}...` : ""}</p> {/* Pending時に送信中メッセージを表示 */}
    </div>
  );
}

// deliverName関数の引数と戻り値の型を定義
async function deliverName(name: string): Promise<string> {
  await new Promise((res) => setTimeout(res, 1000)); // 1秒待機して名前を返す
  return name;
}

// Threadコンポーネントのpropsに型を追加
interface NameItem {
  text: string;
  sending: boolean;
  key?: number;
}

interface ThreadProps {
  names: NameItem[];
  sendName: (formData: FormData) => Promise<void>;
}

function Thread({ names, sendName }: ThreadProps) {
  const formRef = useRef<HTMLFormElement | null>(null);

  // formActionの引数の型をFormDataに設定
  async function formAction(formData: FormData): Promise<void> {
    const newName = formData.get("name") as string;
    addOptimisticName(newName); // 名前を文字列としてoptimisticに追加
    formRef.current?.reset();
    await sendName(formData); // 名前を送信
  }

  // optimisticNamesとaddOptimisticNameに型を付ける
  const [optimisticNames, addOptimisticName] = useOptimistic<NameItem[], string>(
    names,
    (state, newName) => [
      ...state,
      {
        text: newName,
        sending: true,
      },
    ]
  );

  return (
    <>
      {optimisticNames.map((name, index) => (
        <div key={index}>
          {name.text}
          {!!name.sending && <UserNameSubmitForm name={name.text} />} {/* sendingがtrueのときのみ送信中表示 */}
        </div>
      ))}
      <form action={formAction} ref={formRef}>
        <input type="text" name="name" placeholder="Hello!" />
        <button type="submit" disabled={optimisticNames.some((name) => name.sending)}>
          Send
        </button>
      </form>
    </>
  );
}

// SubmitForm コンポーネントのstateとsendName関数の型を追加
export function SubmitForm() {
  const [names, setNames] = useState<NameItem[]>([
    { text: "Hello there!", sending: false, key: 1 },
  ]);

  async function sendName(formData: FormData): Promise<void> {
    const sentName = await deliverName(formData.get("name") as string);
    setNames((names) => [
      ...names,
      { text: sentName, sending: false, key: names.length + 1 },
    ]);
  }

  return <Thread names={names} sendName={sendName} />;
}
