import React from 'react';
import { useActionState } from 'react';

async function increment(previousState: number) {
  return previousState + 1;
}

async function decrement(previousState: number) {
  return previousState - 1;
}

async function reset(previousState: number) {
  return 0;
}

export  function CounterApp() {
  const [state, formAction] = useActionState(increment, 0);

  // 各アクションを実行するためのラップ関数
  const incrementAction = () => formAction(increment);
  const decrementAction = () => formAction(decrement);
  const resetAction = () => formAction(reset);

  return (
    <div>
      <p>Count: {state}</p>
      <form>
        {/* Increment ボタン */}
        <button type="submit" formAction={incrementAction}>Increment</button>

        {/* Decrement ボタン */}
        <button type="submit" formAction={decrementAction}>Decrement</button>

        {/* Reset ボタン */}
        <button type="submit" formAction={resetAction}>Reset</button>
      </form>
    </div>
  );
}
