import { useOptimistic, useState, useRef } from "react";

export async function deliverName(name) {
  console.log(typeof name);
  await new Promise((res) => setTimeout(res, 1000));
  return name;
}

function Thread({ names, sendName }) {
  const formRef = useRef();
  console.log(typeof name);
  async function formAction(formData) {
    console.log(typeof formData);
    addOptimisticName(formData.get("name"));
    formRef.current.reset();
    await sendName(formData);
  }
  const [optimisticNames, addOptimisticName] = useOptimistic(
    names,
    (state, newName) => [
      ...state,
      {
        text: newName,
        sending: true,
      },
    ]
  );

  return (
    <>
      {optimisticNames.map((name, index) => (
        <div key={index}>
          {name.text}
          {!!name.sending && <small> (Sending...)</small>}
        </div>
      ))}
      <form action={formAction} ref={formRef}>
        <input type="text" name="name" placeholder="Hello!" />
        <button type="submit">Send</button>
      </form>
    </>
  );
}

export default function App() {
  const [names, setNames] = useState([
    { text: "Hello there!", sending: false, key: 1 },
  ]);
  async function sendName(formData) {
    const sentName = await deliverName(formData.get("name"));
    setNames((names) => [...names, { text: sentName }]);
  }
  return <Thread names={names} sendName={sendName} />;
}
