import { useActionState } from "react";


async function increment(previousState: number, formData: any): Promise<number> {
  return previousState + 1;
}

async function decrement(previousState: number, formData: any): Promise<number> {
  return previousState - 1;
}

export function CounterApp() {
  // 同じstateを共有
  const [state, handleAction] = useActionState(async (previousState: number, formData: any) => {
    const actionType = formData.get("actionType");
    if (actionType === "increment") {
      return increment(previousState, formData);
    } else if (actionType === "decrement") {
      return decrement(previousState, formData);
    }
    return previousState;
  }, 0);

  return (
    <form>
      <br />
      {state}
      {/* ボタンのname属性でアクションを区別 */}
      <button formAction={handleAction} name="actionType" value="increment">
        stateIncrement
      </button>
      <button formAction={handleAction} name="actionType" value="decrement">
        stateDecrement
      </button>
      <br />
    </form>
  );
  }
