import React, { useState } from "react";
import { useActionState } from "react";

// 非同期関数：incrementとdecrement
async function increment(previousState: number): Promise<number> {
  return previousState + 1;
}

async function decrement(previousState: number): Promise<number> {
  return previousState - 1;
}

function IncrementButton({ onIncrement }: { onIncrement: () => void }) {
  return (
    <button onClick={onIncrement}>Increment</button>
  );
}

function DecrementButton({ onDecrement }: { onDecrement: () => void }) {
  return (
    <button onClick={onDecrement}>Decrement</button>
  );
}

export function CounterApp() {
  // 合計値をstateで管理
  const [total, setTotal] = useState(0);

  // useActionStateフックを使ってインクリメントとデクリメント
  const [incrementState, incrementAction] = useActionState(increment, total);
  const [decrementState, decrementAction] = useActionState(decrement, total);

  // インクリメント用の関数
  const handleIncrement = () => {
    incrementAction();  // incrementActionは非同期で実行され、合計値を更新
  };

  // デクリメント用の関数
  const handleDecrement = () => {
    decrementAction();  // decrementActionは非同期で実行され、合計値を更新
  };

  // 合計値が更新される度に、表示を更新
  React.useEffect(() => {
    setTotal(incrementState);  // incrementStateが更新された時にtotalを更新
  }, [incrementState]);

  React.useEffect(() => {
    setTotal(decrementState);  // decrementStateが更新された時にtotalを更新
  }, [decrementState]);

  return (
    <div>
      <h1>合計値: {total}</h1>
      <IncrementButton onIncrement={handleIncrement} />
      <DecrementButton onDecrement={handleDecrement} />
    </div>
  );
}
