import { useActionState } from 'react';
// Increment
async function increment(previousState: number, formData: FormData): Promise<void> {
  // 状態を更新した後に何も返さないようにする
  return new Promise((resolve) => {
    // 状態更新（前の状態を1増加させる）
    previousState += 1;
    resolve();
  });
}

// Decrement
async function decrement(previousState: number, formData: FormData): Promise<void> {
  // 状態を更新した後に何も返さないようにする
  return new Promise((resolve) => {
    // 状態更新（前の状態を1減少させる）
    previousState -= 1;
    resolve();
  });
}

// Reset
async function reset(previousState: number, formData: FormData): Promise<void> {
  return new Promise((resolve) => {
    // 状態リセット
    previousState = 0;
    resolve();
  });
}
export function CounterApp() {
const [state, formAction] = useActionState(increment, 0); // 初期値 0
  return (
    <form>
      <div>
        <p>{state}</p>
        {/* インクリメントボタン */}
        <button
          type="button"
          formAction={formAction} // Increment
        >
          Increment
        </button>
        {/* デクリメントボタン */}
        <button
          type="button"
          formAction={async (formData: FormData) => {
            // Decrement
            return decrement(state, formData);
          }}
        >
          Decrement
        </button>
        {/* リセットボタン */}
        <button
          type="button"
          formAction={async (formData: FormData) => {
            // Reset
            return reset(state, formData);
          }}
        >
          Reset
        </button>
      </div>
    </form>
  );
}
